-- ----------------------------
-- Table structure for lar_article
-- ----------------------------
DROP TABLE IF EXISTS `lar_article`;
CREATE TABLE `lar_article` (
  `art_id` int(11) NOT NULL AUTO_INCREMENT,
  `cate_id` int(11) NOT NULL DEFAULT '0' COMMENT '文章分类ID',
  `art_title` varchar(100) NOT NULL DEFAULT '' COMMENT '文章标题',
  `art_tag` varchar(100) NOT NULL DEFAULT '' COMMENT 'tag标签',
  `art_keywords` varchar(200) NOT NULL DEFAULT '' COMMENT '关键词',
  `art_description` varchar(200) NOT NULL DEFAULT '' COMMENT '描述',
  `art_thumb` varchar(255) NOT NULL DEFAULT '' COMMENT '缩略图',
  `art_content` text NOT NULL COMMENT '文章内容',
  `art_add_time` int(11) NOT NULL DEFAULT '0' COMMENT '添加时间',
  `art_uptime` int(11) NOT NULL DEFAULT '0' COMMENT '更新时间',
  `art_author` varchar(35) NOT NULL DEFAULT '' COMMENT '作者',
  `art_view` int(11) NOT NULL DEFAULT '0' COMMENT '查看次数',
  PRIMARY KEY (`art_id`)
) ENGINE=MyISAM AUTO_INCREMENT=24 DEFAULT CHARSET=utf8 COMMENT='//文章表';

