<?php $__env->startSection('content'); ?>    <div id="page-wrapper">        <div class="main-page">            <div class="tables">                <div class="bs-example widget-shadow" data-example-id="hoverable-table">                    <h4>                        文章栏目:                        <a href="<?php echo e(url('admin/article/create')); ?>" class="btn btn-primary">添加文章</a>                        <span>文章数量：<strong><?php echo e($count); ?></strong> 篇</span>                        <form method="get" action="<?php echo e(url('admin/article?cate_id')); ?>" id="serform" style="float:left;margin-right:50px;">                            <select name="cate_id" onchange="changeuser()" style="width:auto;" class="form-control1">                                <option value="">全部</option>                                <?php $__currentLoopData = $cate; $__env->addLoop($__currentLoopData); foreach($__currentLoopData as $v): $__env->incrementLoopIndices(); $loop = $__env->getLastLoop(); ?>                                <option value="<?php echo e($v->cate_id); ?>" <?php if($v->cate_id == \Illuminate\Support\Facades\Input::get('cate_id')): ?> selected = "selected" <?php endif; ?> ><?php echo e($v->_cate_name); ?></option>                                <?php endforeach; $__env->popLoop(); $loop = $__env->getLastLoop(); ?>                            </select>                        </form>                    </h4>                    <table class="table table-hover">                        <thead>                        <tr>                            <th>ID</th>                            <th>标题</th>                            <th>栏目</th>                            <th>查看次数</th>                            <th>作者</th>                            <th>添加时间</th>                            <th>更新时间</th>                            <th>操作</th>                        </tr>                        </thead>                        <tbody>                        <?php $__currentLoopData = $data; $__env->addLoop($__currentLoopData); foreach($__currentLoopData as $v): $__env->incrementLoopIndices(); $loop = $__env->getLastLoop(); ?>                            <tr id="del_<?php echo e($v->art_id); ?>">                                <th scope="row"><?php echo e($v->art_id); ?></th>                                <td>                                    <a href="<?php echo e(url('a/'.$v->art_id)); ?>" target="_blank">                                    <?php echo e($v->art_title); ?>                                    </a>                                </td>                                <td>                                    <?php if($v->cate_name): ?>                                        <a href="<?php echo e(url('admin/article?cate_id='.$v->cate_id)); ?>">                                            <?php echo e($v->cate_name); ?>                                        </a>                                    <?php else: ?>                                        未分类                                    <?php endif; ?>                                </td>                                <td><?php echo e($v->art_view); ?></td>                                <td><?php echo e($v->art_author); ?></td>                                <td><?php echo e(date('Y-m-d H:i:s',$v->art_add_time)); ?></td>                                <td><?php echo e(date('Y-m-d H:i:s',$v->art_uptime)); ?></td>                                <td>                                    <a href="<?php echo e(url('admin/article/'.$v->art_id.'/edit')); ?>">修改</a>                                    /                                    <a href="javascript:;" onclick="delArt(<?php echo e($v->art_id); ?>)">删除</a>                                </td>                            </tr>                        <?php endforeach; $__env->popLoop(); $loop = $__env->getLastLoop(); ?>                        </tbody>                    </table>                </div>                <div style="margin:0 auto;">                    <div style="text-align:center;">                        <?php echo e($data->links()); ?>                    </div>                </div>            </div>        </div>    </div>    <script type="text/javascript">        //搜索        function changeuser(){            $("#serform").submit();        }    </script>    <script>        function delArt(art_id){            layer.confirm('您确定要删除这篇文章吗？',{                btn:['确定','取消']  //按钮            },function () {                $.post("<?php echo e(url('admin/article/')); ?>/"+art_id,{                    '_method':'delete',                    '_token':"<?php echo e(csrf_token()); ?>"                },function(data){                    if(data.status==0){                        //location.href = location.href;                        layer.msg(data.msg,{icon:6});                        $("#del_"+art_id).remove();                    }else{                        layer.msg(data.msg,{icon:5});                    }                });            });        }    </script><?php $__env->stopSection(); ?>
