<?php $__env->startSection('content'); ?>    <div id="page-wrapper">        <div class="main-page">            <div class="tables">                <div class="bs-example widget-shadow" data-example-id="hoverable-table">                    <h4>                        系统配置:                        <a href="<?php echo e(url('admin/config/create')); ?>" class="btn btn-primary">添加配置</a>                    </h4>                    <div class="form-body">                        <?php if(is_object($errors)): ?>                            <?php if(count($errors)>0): ?>                                <?php $__currentLoopData = $errors->all(); $__env->addLoop($__currentLoopData); foreach($__currentLoopData as $item): $__env->incrementLoopIndices(); $loop = $__env->getLastLoop(); ?>                                    <h4><?php echo e($item); ?></h4>                                <?php endforeach; $__env->popLoop(); $loop = $__env->getLastLoop(); ?>                            <?php endif; ?>                        <?php else: ?>                            <h4><?php echo e($errors); ?></h4>                        <?php endif; ?>                    </div>                    <table class="table table-hover">                        <thead>                        <tr>                            <th>ID</th>                            <th>配置说明</th>                            <th>参数值</th>                            <th>变量名</th>                            <th>排序</th>                            <th>操作</th>                        </tr>                        </thead>                        <tbody>                        <form action="<?php echo e(url('admin/change')); ?>" method="post" class="form-horizontal">                            <?php echo e(csrf_field()); ?>                            <?php $__currentLoopData = $data; $__env->addLoop($__currentLoopData); foreach($__currentLoopData as $v): $__env->incrementLoopIndices(); $loop = $__env->getLastLoop(); ?>                                <tr id="del_<?php echo e($v->config_id); ?>" class="inbox-page">                                    <th scope="row"><?php echo e($v->config_id); ?></th>                                    <td><?php echo e($v->config_tips); ?></td>                                    <td>                                        <input type="hidden" name="config_id[]" value="<?php echo e($v->config_id); ?>">                                        <?php echo $v->_html; ?>                                    </td>                                    <td><?php echo e($v->config_name); ?></td>                                    <td><?php echo e($v->config_order); ?></td>                                    <td>                                        <a href="<?php echo e(url('admin/config/'.$v->config_id.'/edit')); ?>">修改</a>                                        /                                        <a href="javascript:;" onclick="delConf(<?php echo e($v->config_id); ?>)">删除</a>                                    </td>                                </tr>                            <?php endforeach; $__env->popLoop(); $loop = $__env->getLastLoop(); ?>                            <tr>                                <td colspan="6">                                    <button type="submit" class="btn btn-default">提 交</button>                                </td>                            </tr>                        </form>                        </tbody>                    </table>                </div>                <?php echo e($data->links()); ?>            </div>        </div>    </div>    <script>        function delConf(config_id){            layer.confirm('您确定要删除这篇文章吗？',{                btn:['确定','取消']  //按钮            },function () {                $.post("<?php echo e(url('admin/config/')); ?>/"+config_id,{                    '_method':'delete',                    '_token':"<?php echo e(csrf_token()); ?>"                },function(data){                    if(data.status==0){                        //location.href = location.href;                        layer.msg(data.msg,{icon:6});                        $("#del_"+config_id).remove();                    }else{                        layer.msg(data.msg,{icon:5});                    }                });            });        }    </script><?php $__env->stopSection(); ?>
