<?php $__env->startSection('content'); ?>    <div id="page-wrapper">        <div class="main-page">            <div class="tables">                <div class="bs-example widget-shadow" data-example-id="hoverable-table">                    <h4>                        友情链接:                        <a href="<?php echo e(url('admin/links/create')); ?>" class="btn btn-primary">添加链接</a>                    </h4>                    <table class="table table-hover">                        <thead>                        <tr>                            <th>ID</th>                            <th>网站名称</th>                            <th>备注</th>                            <th>网站地址</th>                            <th>排序</th>                            <th>显示</th>                            <th>添加时间</th>                            <th>操作</th>                        </tr>                        </thead>                        <tbody>                        <?php $__currentLoopData = $data; $__env->addLoop($__currentLoopData); foreach($__currentLoopData as $v): $__env->incrementLoopIndices(); $loop = $__env->getLastLoop(); ?>                            <tr id="del_<?php echo e($v->link_id); ?>">                                <th scope="row"><?php echo e($v->link_id); ?></th>                                <td>                                    <a href="<?php echo e($v->link_url); ?>" target="_blank"><?php echo e($v->link_name); ?></a>                                </td>                                <td><?php echo e($v->link_title); ?></td>                                <td><?php echo e($v->link_url); ?></td>                                <td><?php echo e($v->link_order); ?></td>                                <td><?php echo e($v->link_show); ?></td>                                <td><?php echo e(date('Y-m-d H:i:s',$v->link_time)); ?></td>                                <td>                                    <a href="<?php echo e(url('admin/links/'.$v->link_id.'/edit')); ?>">修改</a>                                    /                                    <a href="javascript:;" onclick="delLink(<?php echo e($v->link_id); ?>)">删除</a>                                </td>                            </tr>                        <?php endforeach; $__env->popLoop(); $loop = $__env->getLastLoop(); ?>                        </tbody>                    </table>                </div>                <?php echo e($data->links()); ?>            </div>        </div>    </div>    <script>        function delLink(link_id){            layer.confirm('您确定要删除这篇文章吗？',{                btn:['确定','取消']  //按钮            },function () {                $.post("<?php echo e(url('admin/links/')); ?>/"+link_id,{                    '_method':'delete',                    '_token':"<?php echo e(csrf_token()); ?>"                },function(data){                    if(data.status==0){                        //location.href = location.href;                        layer.msg(data.msg,{icon:6});                        $("#del_"+link_id).remove();                    }else{                        layer.msg(data.msg,{icon:5});                    }                });            });        }    </script><?php $__env->stopSection(); ?>
