<?php $__env->startSection('content'); ?>    <div id="page-wrapper">        <div class="main-page">            <div class="tables">                <div class="bs-example widget-shadow" data-example-id="hoverable-table">                    <h4>                        文章栏目:                        <a href="<?php echo e(url('admin/category/create')); ?>" class="btn btn-primary">添加栏目</a>                    </h4>                    <table class="table table-hover">                        <thead>                        <tr>                            <th>分类名称</th>                            <th>分类标题</th>                            <th>查看次数</th>                            <th>排序</th>                            <th>操作</th>                        </tr>                        </thead>                        <tbody>                        <?php $__currentLoopData = $data; $__env->addLoop($__currentLoopData); foreach($__currentLoopData as $v): $__env->incrementLoopIndices(); $loop = $__env->getLastLoop(); ?>                        <tr id="del_<?php echo e($v->cate_id); ?>">                            <td>                                <a href="<?php echo e(url('admin/article?cate_id='.$v->cate_id)); ?>">                                    <?php if($v->lave!=0): ?> <?php echo '┝'.str_repeat('━',$v->lave)?> <?php endif; ?><?php echo e($v->cate_name); ?>                                </a>                                (<?php echo e($v->art); ?>)                            </td>                            <td><?php echo e($v->cate_title); ?></td>                            <td><?php echo e($v->cate_view); ?></td>                            <td><?php echo e($v->cate_order); ?></td>                            <td>                                <a href="<?php echo e(url('admin/category/'.$v->cate_id.'/edit')); ?>">修改</a>                                /                                <a href="javascript:;" onclick="delCate(<?php echo e($v->cate_id); ?>)">删除</a>                            </td>                        </tr>                        <?php endforeach; $__env->popLoop(); $loop = $__env->getLastLoop(); ?>                        </tbody>                    </table>                </div>            </div>        </div>    </div>    <script>        function delCate(cate_id){            layer.confirm('您确定要删除这篇文章吗？',{                btn:['确定','取消']  //按钮            },function () {                $.post("<?php echo e(url('admin/category/')); ?>/"+cate_id,{                    '_method':'delete',                    '_token':"<?php echo e(csrf_token()); ?>"                },function(data){                    if(data.status==0){                        //location.href = location.href;                        layer.msg(data.msg,{icon:6});                        $("#del_"+cate_id).remove();                    }else{                        layer.msg(data.msg,{icon:5});                    }                });            });        }    </script><?php $__env->stopSection(); ?>
